package main

import (
	"fmt"
	"quic"
)

func main() {
	rfc9001ClientInitialDecrypt()
	rfc9001ClientInitialEncrypt()
}

func rfc9001ClientInitialDecrypt() {
	var tlsinfo quic.TLSInfo
	destconnID := quic.StrtoByte("8394C8F03E515708")
	tlsinfo.QuicKeyBlock = quic.CreateQuicInitialSecret(destconnID)

	encClientInitial := quic.StrtoByte("")

	quic.ParseRawQuicPacket2(encClientInitial, true, tlsinfo)

}

func rfc9001ClientInitialEncrypt() {
	var tlsinfo quic.TLSInfo
	destconnID := quic.StrtoByte("8394C8F03E515708")
	tlsinfo.QuicKeyBlock = quic.CreateQuicInitialSecret(destconnID)

	clientcrypto := quic.StrtoByte("")
	padding := quic.AppendPaddingFrame(clientcrypto, 1162-len(clientcrypto))
	fmt.Printf("after packet length is %d\n", len(padding))

	header := quic.StrtoByte("c300000001088394c8f03e5157080000449e00000002")

	enc := quic.EncryptClientPayload([]byte{0x02}, header, padding, tlsinfo.QuicKeyBlock)
	fmt.Printf("enc is %x\n", enc[0:16])

	packet := header
	packet = append(packet, enc...)

	fmt.Printf("before unprotected packet is %x\n", packet)

	result := quic.ProtectHeader(len(header)-4, packet, tlsinfo.QuicKeyBlock.ClientHeaderProtection, true)
	fmt.Printf("result is %x\n", result)

}
